package main

import (
	"reflect"
	"strings"
	"testing"
)

func TestLongestCommonSubsequence(t *testing.T) {
	tests := []struct {
		input string
		want  string
	}{
		{
			input: "3\nabcbdab\nbdcaba\nabc\nabc\nabc\nbc\n",
			want:  "4\n3\n2",
		},
		{
			input: "12\na\na\nbbb\nbbb\naba\nbbaba\nabababbbaa\nbbabaabba\na\naaaaa\naizojfeiz\naaaizojifeizz\nzzyiioxizu\nzzsyoizuo\niooooioiooiio\nooiiioioiooooiioio\najfoaooieellzljieoaelj\najoiiffoaolliseellzljieoaelj\nzzzzzyyzyy\nzzyyyyz\nabcdef\nfedcba\nabc\nxyz\n",
			want:  "1\n3\n3\n7\n1\n9\n7\n11\n21\n6\n1\n0",
		},
		{
			input: "3\\\\\\\n",
			want:  "999\n988\n1000",
		},
	}

	for _, test := range tests {
		r := strings.NewReader(test.input)
		if result := answer(r); !reflect.DeepEqual(result, test.want) {
			t.Errorf("\ngot =%q\nwant=%q", result, test.want)
		}
	}
}
